DROP TABLE IF EXISTS `%DB_PREFIX%api_login`;
CREATE TABLE `%DB_PREFIX%api_login` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL,
  `config` text NOT NULL,
  `class_name` varchar(255) NOT NULL,
  `is_weibo` tinyint(1) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='安装的微博接口';
DROP TABLE IF EXISTS `%DB_PREFIX%ask`;
CREATE TABLE `%DB_PREFIX%ask` (
  `id` int(11) NOT NULL auto_increment,
  `title` varchar(255) NOT NULL COMMENT '提问的标题',
  `content` text NOT NULL COMMENT '提问的内容',
  `user_id` int(11) NOT NULL COMMENT '提问的用户ID',
  `nickname` varchar(100) NOT NULL COMMENT '提问人的昵称（冗余字段）',
  `create_time` int(11) NOT NULL COMMENT '提问时间',
  `reply_content` text NOT NULL COMMENT '管理员回复',
  `reply_time` int(11) NOT NULL COMMENT '回复时间',
  `is_recommend` tinyint(1) NOT NULL COMMENT '推荐的问题(用于首页等页面调用)',
  `ask_type_id` int(11) NOT NULL COMMENT '提问的分类ID',
  `is_reply` tinyint(1) default NULL COMMENT '是否已回复(0 未回复，1已回复)',
  PRIMARY KEY  (`id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `reply_time` USING BTREE (`reply_time`),
  KEY `is_recommend` USING BTREE (`is_recommend`),
  KEY `ask_type_id` USING BTREE (`ask_type_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='会员提问表';
DROP TABLE IF EXISTS `%DB_PREFIX%ask_conf`;
CREATE TABLE `%DB_PREFIX%ask_conf` (
  `ASK_MONEY` int(11) NOT NULL COMMENT '提问被回复获取的现金',
  `ASK_SCORE` int(11) NOT NULL COMMENT '提问被回复获取的积分',
  `ASK_EXP` int(11) NOT NULL COMMENT '提问被回复获取的经验'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='提问被采纳，回复，推荐时，后台有管理员操作的是否奖励选项，管理员同意后发放奖励，用于app_conf调用';
