DROP TABLE IF EXISTS `%DB_PREFIX%comment`;
CREATE TABLE `%DB_PREFIX%comment` (
  `id` int(11) NOT NULL auto_increment,
  `content` text NOT NULL COMMENT '评论的内容',
  `comment_type` tinyint(1) NOT NULL COMMENT '评论的类型(1:游记评论 2:动态瀑布流评论 3:文章资讯评论 )',
  `comment_rel_id` int(11) NOT NULL COMMENT '相关的ID',
  `user_id` int(11) NOT NULL COMMENT '评论的会员ID',
  `nickname` varchar(100) NOT NULL COMMENT '昵称，冗余字段',
  `create_time` int(11) NOT NULL COMMENT '评论时间',
  PRIMARY KEY  (`id`),
  KEY `comment_type` USING BTREE (`comment_type`),
  KEY `comment_rel_id` USING BTREE (`comment_rel_id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `create_time` USING BTREE (`create_time`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='会员评论表';
