DROP TABLE IF EXISTS `%DB_PREFIX%company_tour`;
CREATE TABLE `%DB_PREFIX%company_tour` (
  `id` int(11) NOT NULL auto_increment,
  `company_name` varchar(255) NOT NULL COMMENT '出游的公司名称',
  `company_contact` varchar(255) NOT NULL COMMENT '联系人',
  `company_mobile` varchar(255) NOT NULL COMMENT '手机',
  `area_code` varchar(255) default NULL COMMENT '区号',
  `company_tel` varchar(255) default NULL COMMENT '电话',
  `company_qq` varchar(255) NOT NULL COMMENT 'qq',
  `company_address` varchar(255) default NULL COMMENT '公司地址',
  `start_city` varchar(255) NOT NULL COMMENT '出发城市,使用下拉框或弹出的形式选取',
  `people_num_min` int(11) NOT NULL COMMENT '最大出游人数',
  `people_num_max` int(11) NOT NULL COMMENT '最小出游人数',
  `budget_min` int(11) default NULL COMMENT '最大预算',
  `budget_max` int(11) default NULL COMMENT '最小预算',
  `tour_date` varchar(255) NOT NULL COMMENT '预计出游的时间',
  `tour_dest` varchar(255) NOT NULL COMMENT '出游目的地（表示为文本框，让用户自行填写，可填多个）',
  `other_require` text COMMENT '其他要求',
  `tour_days` int(11) NOT NULL COMMENT '预计出游天数',
  `create_time` int(11) NOT NULL COMMENT '发表时间',
  `status` tinyint(11) NOT NULL COMMENT '给管理员标准的状态0:新提交 1:跟进中 2:已签约',
  `user_id` int(11) default NULL COMMENT '可能存在的发布人ID',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='公司旅游的提交信息';
DROP TABLE IF EXISTS `%DB_PREFIX%conf`;
CREATE TABLE `%DB_PREFIX%conf` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL,
  `value` text NOT NULL,
  `group_id` int(11) NOT NULL,
  `input_type` tinyint(1) NOT NULL COMMENT '配置输入的类型 0:文本输入 1:下拉框输入 2:图片上传 3:编辑器',
  `value_scope` text NOT NULL COMMENT '取值范围',
  `is_conf` tinyint(1) NOT NULL COMMENT '是否可配置 0: 可配置  1:不可配置',
  `sort` int(11) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=191 DEFAULT CHARSET=utf8;
