DROP TABLE IF EXISTS `%DB_PREFIX%hotel`;
CREATE TABLE `%DB_PREFIX%hotel` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL COMMENT '酒店名称',
  `city_id` int(11) NOT NULL COMMENT '所在城市',
  `description` text NOT NULL COMMENT '酒店的描述',
  `city_match` text NOT NULL COMMENT '用于搜索匹配的城市拼音全文索引',
  `city_match_row` text NOT NULL COMMENT '原文',
  `tag` text NOT NULL COMMENT '模糊搜索的标签',
  `tag_match` text NOT NULL COMMENT '标签的unicode全文索引',
  `tag_match_row` text NOT NULL COMMENT '原文',
  `area_match` text NOT NULL COMMENT '旅游大区name的unicode全文索引(冗余)',
  `area_match_row` text NOT NULL COMMENT '原文',
  `place_match` text NOT NULL COMMENT '旅游小区的name的unicode全文索引',
  `place_match_row` text NOT NULL COMMENT '原文',
  `star_level` tinyint(1) NOT NULL COMMENT '酒店星级 0-7',
  `address` varchar(255) NOT NULL COMMENT '详细地址',
  `x_point` varchar(255) NOT NULL COMMENT '纬度',
  `y_point` varchar(255) NOT NULL COMMENT '经度',
  `price` int(11) NOT NULL COMMENT '最低收费价格',
  `image` varchar(255) NOT NULL COMMENT '图片',
  `brief` varchar(255) NOT NULL COMMENT '简单描述',
  `hotel_begin_time` date NOT NULL COMMENT '开业时间',
  `satify` int(11) NOT NULL COMMENT '满意度百分比 冗余（所有点评总得分/点评人数*5*10000）保存小数后两位',
  `review_total` int(11) NOT NULL COMMENT '点评总数 冗余',
  `tel` varchar(50) NOT NULL COMMENT '联系电话',
  `image_list` text NOT NULL COMMENT '图库列表，序列化冗余',
  `supplier_id` int(11) NOT NULL COMMENT '商家ID',
  PRIMARY KEY  (`id`),
  KEY `city_id` USING BTREE (`city_id`),
  KEY `star_level` USING BTREE (`star_level`),
  KEY `price` USING BTREE (`price`),
  KEY `satify` USING BTREE (`satify`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  FULLTEXT KEY `tag_match` (`tag_match`),
  FULLTEXT KEY `area_match` (`area_match`),
  FULLTEXT KEY `place_match` (`place_match`),
  FULLTEXT KEY `city_match` (`city_match`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='酒店表';
