DROP TABLE IF EXISTS `%DB_PREFIX%hotel_room`;
CREATE TABLE `%DB_PREFIX%hotel_room` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL COMMENT '房间名称',
  `brief` varchar(255) NOT NULL COMMENT '描述',
  `image` varchar(255) NOT NULL COMMENT '房间图片',
