  `sale_total` int(11) NOT NULL COMMENT '销量',
  `sale_virtual_total` int(11) NOT NULL COMMENT '虚拟销量',
  `floor` varchar(50) NOT NULL COMMENT '楼层',
  `wifi` tinyint(1) NOT NULL COMMENT '是否有免费网络0/1',
  `roomarea` int(5) NOT NULL COMMENT '面积（平方米）',
  `bedtype` tinyint(1) NOT NULL COMMENT '床型',
  `hotel_id` int(11) NOT NULL COMMENT '酒店ID',
  `hotel_name` varchar(255) NOT NULL COMMENT '酒店名称，冗余',
  `hotel_image` varchar(255) NOT NULL COMMENT '酒店图片，冗余',
  `hotel_tag_match` text NOT NULL COMMENT '酒店标签全文索引，冗余',
  `hotel_city_id` int(11) NOT NULL COMMENT '酒店所在城市',
  `hotel_city_match` text NOT NULL COMMENT '城市全文索引 冗余',
  `hotel_area_match` text NOT NULL COMMENT '酒店大区域，冗余',
  `hotel_place_match` text NOT NULL COMMENT '酒店小区域,冗余',
  `hotel_price` int(11) NOT NULL COMMENT '酒店起价，冗余',
  `hotel_start_level` tinyint(1) NOT NULL COMMENT '酒店星级，冗余',
  `hotel_address` varchar(255) NOT NULL COMMENT '酒店地址，冗余',
  `hotel_x_point` varchar(255) NOT NULL COMMENT '酒店纬度，冗余',
  `hotel_y_point` varchar(255) NOT NULL COMMENT '酒店经度，冗余',
  `return_money` int(11) NOT NULL COMMENT '返现金(返还需在退算结算后发放)',
  `return_score` int(11) NOT NULL COMMENT '返积分',
  `return_exp` int(11) NOT NULL COMMENT '返经验',
  `is_effect` tinyint(1) NOT NULL COMMENT '禁用则不再显示该房间信息',
  `supplier_id` int(11) NOT NULL COMMENT '商家ID',
  `is_history` tinyint(1) NOT NULL COMMENT '是否为往期产品，1表示显示但不能购买',
  `is_tuan` tinyint(1) NOT NULL COMMENT '是否为团购产品',
  `tuan_begin_time` int(11) NOT NULL COMMENT '团购开始时间',
  `tuan_end_time` int(11) NOT NULL COMMENT '团购结束时间',
  `tuan_success_count` int(11) NOT NULL COMMENT '成团数量',
  `tuan_is_pre` tinyint(1) NOT NULL COMMENT '是否预告团购',
  `sale_max` int(11) NOT NULL COMMENT '最大库存',
  `sort` int(11) NOT NULL COMMENT '排序',
  `end_time` int(11) NOT NULL COMMENT '使用过期时间',
  `end_time_day` int(11) NOT NULL COMMENT '自预定日起的过期天数',
  `appoint_begin_time` int(11) NOT NULL COMMENT '预约的开始时间(表示为入住时间的检索与预约时的下单条件)',
  `appoint_end_time` int(11) NOT NULL COMMENT '预约的结束时间',
  `is_review_return` tinyint(1) NOT NULL COMMENT '允许点评返还，界面上选是时，自动加载点评的配置',
  `review_return_money` int(11) NOT NULL COMMENT '点评返还现金',
  `review_return_score` int(11) NOT NULL COMMENT '点评返积分',
  `review_return_exp` int(11) NOT NULL COMMENT '点评返还经验',
  `is_rebate` tinyint(1) NOT NULL COMMENT '允许邀请返利',
  `is_buy_return` tinyint(1) NOT NULL COMMENT '允许购物返还',
  PRIMARY KEY  (`id`),
  KEY `origin_price` USING BTREE (`origin_price`),
  KEY `current_price` USING BTREE (`current_price`),
  KEY `sale_price` USING BTREE (`sale_price`),
  KEY `pay_type` USING BTREE (`pay_type`),
  KEY `wifi` USING BTREE (`wifi`),
  KEY `hotel_city_id` USING BTREE (`hotel_city_id`),
  KEY `hotel_price` USING BTREE (`hotel_price`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  KEY `is_effect` USING BTREE (`is_effect`),
  KEY `is_history` USING BTREE (`is_history`),
  KEY `is_tuan` USING BTREE (`is_tuan`),
  KEY `tuan_begin_time` USING BTREE (`tuan_begin_time`),
  KEY `tuan_end_time` USING BTREE (`tuan_end_time`),
  KEY `tuan_is_pre` USING BTREE (`tuan_is_pre`),
  KEY `sort` USING BTREE (`sort`),
  FULLTEXT KEY `hotel_tag_match` (`hotel_tag_match`),
  FULLTEXT KEY `hotel_area_match` (`hotel_area_match`),
  FULLTEXT KEY `hotel_place_match` (`hotel_place_match`),
  FULLTEXT KEY `hotel_city_match` (`hotel_city_match`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='酒店房间表, 酒店的搜索直接搜索该表，可以过滤入住时间';
