DROP TABLE IF EXISTS `%DB_PREFIX%hotel_room_order`;
CREATE TABLE `%DB_PREFIX%hotel_room_order` (
  `id` int(11) NOT NULL,
  `sn` varchar(255) NOT NULL COMMENT '酒店房间订单号(H_订单号)',
  `total_price` int(11) NOT NULL COMMENT '应付总额(item_price)',
  `pay_amount` int(11) NOT NULL COMMENT '已付金额(online_pay+voucher_pay+account_pay)',
  `user_id` int(11) NOT NULL COMMENT '会员ID',
  `item_price` int(11) NOT NULL COMMENT '商品本身的价格',
  `online_pay` int(11) NOT NULL COMMENT '在线支付金额',
  `voucher_pay` int(11) NOT NULL COMMENT '代金券支付(超出部份不显示，如代金券100，应付50，此处为50，但代金券直接失效，超出不退)',
  `account_pay` int(11) NOT NULL COMMENT '余额支付部份',
  `refund_amount` int(11) NOT NULL COMMENT '已退金额',
  `order_status` tinyint(1) NOT NULL default '1' COMMENT '订单状态(流程)1.新订单 2.已确认 3.已完成 4.作废\r\n新订单：未确认（包含已付款）的都表示为新订单\r\n已确认：表示为商家或管理员查看，确认手动修改\r\n新订单、已确认均可申请退款，否则不可退款，退款条件还需考虑到具体商品是否支付，退款审核需由管理员最终确认\r\n已完成：当所有订单商品状态为已完成，自动同步成已完成\r\n作废：新订单未付款/已完成 订单可以操作为作废，作废订单可删除,不可逆',
  `pay_status` tinyint(1) NOT NULL COMMENT '支付状态 \r\n0.未支付(代金券或余额支付仍算未支付) \r\n1.已支付(pay_amount==total_price)',
  `create_time` int(11) NOT NULL COMMENT '下单时间',
  `pay_time` int(11) NOT NULL COMMENT '支付时间',
  `confirm_time` int(11) NOT NULL COMMENT '定单确认时间',
  `over_time` int(11) NOT NULL COMMENT '全部完成时间',
  `appoint_name` varchar(50) NOT NULL COMMENT '预定人姓名',
  `appoint_mobile` varchar(50) NOT NULL COMMENT '预定人手机',
  `appoint_email` varchar(100) NOT NULL COMMENT '预约人邮箱',
  `supplier_id` int(11) NOT NULL COMMENT '商户ID',
  `review_return_money` int(11) NOT NULL COMMENT '点评审核后获取的现金',
  `review_return_score` int(11) NOT NULL COMMENT '点评审核后获取的积分',
  `review_return_exp` int(11) NOT NULL COMMENT '点评审核后获取的经验',
  `review_return_voucher_type_id` int(11) NOT NULL COMMENT '点评审核后获取的代金券类型ID',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `uk_sn` USING BTREE (`sn`),
  KEY `sn` USING BTREE (`sn`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `order_status` USING BTREE (`order_status`),
  KEY `pay_status` USING BTREE (`pay_status`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `pay_time` USING BTREE (`pay_time`),
  KEY `confirm_time` USING BTREE (`confirm_time`),
  KEY `over_time` USING BTREE (`over_time`),
  KEY `supplier_id` USING BTREE (`supplier_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='酒店房间订单表';
DROP TABLE IF EXISTS `%DB_PREFIX%hotel_room_order_item`;
CREATE TABLE `%DB_PREFIX%hotel_room_order_item` (
  `id` int(11) NOT NULL auto_increment,
