  `id` int(11) NOT NULL auto_increment COMMENT '资讯分类名称',
  `name` varchar(255) NOT NULL COMMENT '资讯分类名称',
  `is_recommend` tinyint(1) NOT NULL COMMENT '分类是否推荐到新闻首页',
  `sort` int(11) NOT NULL,
  `is_focus` tinyint(1) NOT NULL COMMENT '是否为动态资讯(新闻首页轮播右侧)',
  `is_index` tinyint(1) NOT NULL COMMENT '是否推荐到网站首页',
  PRIMARY KEY  (`id`),
  KEY `is_recommend` USING BTREE (`is_recommend`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='资讯分类';
