DROP TABLE IF EXISTS `%DB_PREFIX%review`;
CREATE TABLE `%DB_PREFIX%review` (
  `id` int(11) NOT NULL auto_increment,
  `user_id` int(11) NOT NULL COMMENT '点评会员ID',
  `nickname` varchar(50) NOT NULL COMMENT '会员昵称（冗余字段）',
  `review_content` text NOT NULL COMMENT '点评内容',
  `review_time` int(11) NOT NULL COMMENT '点评时间',
  `review_type` tinyint(1) NOT NULL default '1' COMMENT '点评类型(1.线路 2.门票 3.酒店)',
  `review_rel_id` int(11) NOT NULL COMMENT '相关类型的ID，如type为1则表示线路的ID',
  `point` tinyint(1) NOT NULL default '1' COMMENT '得分 1-5',
  `group_point` text NOT NULL COMMENT '分组评分的序列化结果（冗余字段）',
  `image_list` text NOT NULL COMMENT '序列化的图片列表，用于冗余字段（一般存三张图片）',
  `image_count` int(11) NOT NULL COMMENT '点评的图片数量(0表示无图) 冗余计数',
  `is_verify` tinyint(1) NOT NULL COMMENT '是否已审核，用于表示是否要在前台显示',
  `review_ip` varchar(20) NOT NULL default '0.0.0.0' COMMENT '点评人IP',
  `review_reply` text NOT NULL COMMENT '点评的回复(来自管理员)',
  `review_reply_time` int(11) NOT NULL COMMENT '点评回复时间',
  `useful_count` int(11) NOT NULL COMMENT '有用计数(赞)',
  `money` int(11) NOT NULL COMMENT '点评获赠的现金（审核通过后获取）',
  `score` int(11) NOT NULL COMMENT '点评获赠的积分（审核通过后获取）',
  `exp` int(11) NOT NULL COMMENT '点评后获取的经验(审核后获取)',
  `voucher_type_id` int(11) NOT NULL COMMENT '点评获取的代金券ID',
  `voucher_name` varchar(255) NOT NULL COMMENT '返还后保存的代金券名称',
  `voucher_count` int(11) NOT NULL COMMENT '返还后显示的代金券面额',
  `review_rel_name` varchar(255) default NULL COMMENT '关联名称',
  PRIMARY KEY  (`id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `review_time` USING BTREE (`review_time`),
  KEY `review_type` USING BTREE (`review_type`),
  KEY `review_rel_id` USING BTREE (`review_rel_id`),
  KEY `point` USING BTREE (`point`),
  KEY `is_verify` USING BTREE (`is_verify`),
  KEY `image_count` USING BTREE (`image_count`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='点评表';
