DROP TABLE IF EXISTS `%DB_PREFIX%spot_supplier`;
CREATE TABLE `%DB_PREFIX%spot_supplier` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(200) NOT NULL COMMENT '景点名称',
  `tag` text NOT NULL COMMENT '标签，用于模糊查询的分词',
  `tag_match` text NOT NULL COMMENT '标签的unicode全文索引',
  `tag_match_row` text NOT NULL COMMENT '原文',
  `image` varchar(255) NOT NULL COMMENT '图片',
  `image_list` text NOT NULL COMMENT '图片列表序列号，冗余',
  `city_match` text NOT NULL COMMENT '城市拼音的冗余索引',
  `city_match_row` text NOT NULL COMMENT '原文',
  `area_match` text NOT NULL COMMENT '区域拼音的全文索引冗余',
  `area_match_row` text NOT NULL COMMENT '原文',
  `place_match` text NOT NULL COMMENT '景点小区域关联冗余索引',
  `place_match_row` text NOT NULL COMMENT '原文',
  `cate_match` text NOT NULL COMMENT '分类的unicode全文索引冗余',
  `cate_match_row` text NOT NULL COMMENT '原文',
  `has_ticket` tinyint(1) NOT NULL COMMENT '是否有门票销售(0:纯展示的景点 1:门票销售)',
  `ticket_list` text NOT NULL COMMENT '景点门票列表序列化，冗余',
  `ticket_price` int(11) NOT NULL COMMENT '最低价（所有门票产品的最低价，用于参考显示, 冗余）',
  `address` varchar(255) NOT NULL COMMENT '详细地址',
  `x_point` varchar(255) NOT NULL COMMENT '纬度',
  `y_point` varchar(255) NOT NULL COMMENT '经度',
  `description` text NOT NULL COMMENT '描述详情',
  `brief` varchar(255) NOT NULL COMMENT '简介',
  `appointment_desc` text NOT NULL COMMENT '预订需知',
  `spot_desc_1` text NOT NULL COMMENT '交通信息',
  `spot_desc_1_name` varchar(255) NOT NULL,
  `spot_desc_2` text NOT NULL,
  `spot_desc_2_name` varchar(50) NOT NULL,
  `spot_desc_3` text NOT NULL,
  `spot_desc_3_name` varchar(50) NOT NULL,
  `spot_desc_4` text NOT NULL,
  `spot_desc_4_name` varchar(255) NOT NULL,
  `spot_level` tinyint(1) NOT NULL COMMENT '评级 1-5 A - 5A级景区',
  `supplier_name` varchar(200) NOT NULL COMMENT '商家名称，冗余',
  `supplier_id` int(11) NOT NULL COMMENT '商家ID',
  `rel_id` int(11) NOT NULL COMMENT '如果供应商编辑门票那么这个即为关联景点门票的id',
  `create_time` int(11) NOT NULL COMMENT '提交时间',
  PRIMARY KEY  (`id`),
  KEY `spot_level` USING BTREE (`spot_level`),
  KEY `has_ticket` USING BTREE (`has_ticket`),
  FULLTEXT KEY `city_match` (`city_match`),
  FULLTEXT KEY `area_match` (`area_match`),
  FULLTEXT KEY `place_match` (`place_match`),
  FULLTEXT KEY `cate_match` (`cate_match`),
  FULLTEXT KEY `tag_match` (`tag_match`)
