DROP TABLE IF EXISTS `%DB_PREFIX%ticket`;
CREATE TABLE `%DB_PREFIX%ticket` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL COMMENT '门票名称',
  `short_name` varchar(255) NOT NULL COMMENT '用于邮件短信发送时显示的名称',
  `origin_price` int(11) NOT NULL COMMENT '原价',
  `current_price` int(11) NOT NULL COMMENT '现价',
  `spot_id` int(11) NOT NULL COMMENT '景点ID',
  `spot_name` varchar(255) NOT NULL COMMENT '景点名称，由商家申请时填写的景点名称',
  `sale_price` int(11) NOT NULL COMMENT '销售价(如果pay_type为2，表示为定金)',
  `pay_type` tinyint(1) NOT NULL default '1' COMMENT '预定方式 (1.全款在线支付(只有在线付全款的才可选是否配送) 2.定金支付到景点付全款 3.景点支付)',
  `sale_total` int(11) NOT NULL COMMENT '总销量(不计算已退款，冗余)',
  `sale_virtual_total` int(11) NOT NULL COMMENT '虚拟销售量，只作增加操作',
  `supplier_id` int(11) NOT NULL COMMENT '商家ID',
  `is_appoint_time` tinyint(1) NOT NULL COMMENT '是否需要填写预约到场时间\r\n是. 则不需设置 end_time和end_time_day，门票的end_time为预约时间，允许改签\r\n否. 需要设置end_time和end_time_day, 不允许改签',
  `is_end_time` tinyint(1) NOT NULL,
  `end_time` int(11) NOT NULL COMMENT '有效期截止日(字段更新不影响已销售的门票)，0表示无限期',
  `end_time_day` int(11) NOT NULL COMMENT '过期时间自预定日起的天数',
  `refund_desc` varchar(255) NOT NULL COMMENT '退票的说明（如在xxx时候前可退票）',
  `is_refund` tinyint(1) NOT NULL COMMENT '是否支持退票，改',
  `is_expire_refund` tinyint(1) NOT NULL COMMENT '过期，即end_time过期后才可退款',
  `is_delivery` tinyint(1) NOT NULL COMMENT '是否允许配送(运费只计算商家的设置，同一商家门票可一同配置)，如为0表示只能到点取票, 并发送验证信息，识别码，用于商家验证',
  `create_time` int(11) NOT NULL COMMENT '添加时间(未审核表示提交时间，审核通过以审核通过的时间为准)',
  `return_money` int(11) NOT NULL COMMENT '返现金(所有的返还均要在商家确认订单完成后发放)',
  `return_score` int(11) NOT NULL COMMENT '返积分',
  `return_exp` int(11) NOT NULL COMMENT '获得的经验值',
  `is_effect` tinyint(1) NOT NULL COMMENT '下架不再显示',
  `is_history` tinyint(1) NOT NULL COMMENT '禁止销售，0.可销售 1.显示在往期列表，但不能购买',
  `min_buy` int(11) NOT NULL COMMENT '最小购买张数',
  `max_buy` int(11) NOT NULL COMMENT '最大购买张数',
  `sort` int(11) NOT NULL COMMENT '排序，越大越靠前',
  `is_tuan` tinyint(1) NOT NULL COMMENT '是否为团购门票',
  `tuan_cate` int(11) NOT NULL,
  `tuan_begin_time` int(11) NOT NULL COMMENT '团购开始时间，用于预告',
  `tuan_end_time` int(11) NOT NULL COMMENT '团购结束时间',
  `tuan_success_count` int(11) NOT NULL COMMENT '成团数量',
  `tuan_is_pre` tinyint(1) NOT NULL COMMENT '是否预告',
  `sale_max` int(11) NOT NULL COMMENT '库存',
  `is_review_return` tinyint(1) NOT NULL COMMENT '是否允许点评返还',
  `review_return_money` int(11) NOT NULL COMMENT '点评返还的现金',
  `review_return_score` int(11) NOT NULL COMMENT '点评返还的积分',
  `review_return_exp` int(11) NOT NULL COMMENT '点评返还经验',
  `is_rebate` tinyint(1) NOT NULL COMMENT '允许邀请返利',
  `is_buy_return` tinyint(1) NOT NULL COMMENT '允许购物返还',
  `is_divide` tinyint(1) NOT NULL COMMENT '是否允许多张门票分次使用 1：生成多个验证码 0:生成一个验证码',
  `name_brief` varchar(255) NOT NULL COMMENT '标题简介',
  `order_status` tinyint(1) NOT NULL default '1' COMMENT '2.自动确认 1.手动确认',
  `show_in_api` tinyint(1) NOT NULL COMMENT '是否显示在API',
  `paper_must` tinyint(1) NOT NULL COMMENT '证件号是否必填',
  `tuan_id` int(11) NOT NULL COMMENT '团购ID',
  PRIMARY KEY  (`id`),
  KEY `current_price` USING BTREE (`current_price`),
  KEY `origin_price` USING BTREE (`origin_price`),
  KEY `spot_id` USING BTREE (`spot_id`),
  KEY `sale_price` USING BTREE (`sale_price`),
  KEY `pay_type` USING BTREE (`pay_type`),
  KEY `sale_total` USING BTREE (`sale_total`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  KEY `end_time` USING BTREE (`end_time`),
  KEY `is_refund` USING BTREE (`is_refund`),
  KEY `is_delivery` USING BTREE (`is_delivery`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `is_expire_refund` USING BTREE (`is_expire_refund`),
  KEY `is_history` USING BTREE (`is_history`),
  KEY `is_tuan` USING BTREE (`is_tuan`),
  KEY `tuan_begin_time` USING BTREE (`tuan_begin_time`),
  KEY `tuan_end_time` USING BTREE (`tuan_end_time`),
  KEY `tuan_is_pre` USING BTREE (`tuan_is_pre`),
  KEY `sort` USING BTREE (`sort`),
  KEY `show_in_api` USING BTREE (`show_in_api`)
) ENGINE=MyISAM AUTO_INCREMENT=26 DEFAULT CHARSET=utf8 COMMENT='门票表';
