CREATE TABLE `%DB_PREFIX%ticket_order_item` (
  `id` int(11) NOT NULL auto_increment,
  `ticket_id` int(11) NOT NULL COMMENT '门票产品ID',
  `ticket_name` varchar(255) NOT NULL COMMENT '门票名称，当时下单时的门票名称',
  `order_id` int(11) NOT NULL COMMENT '订单ID',
  `user_id` int(11) NOT NULL COMMENT '下单人ID',
  `begin_time` int(11) NOT NULL COMMENT '开始时间',
  `end_time` int(11) NOT NULL COMMENT '过期时间',
  `verify_code` varchar(10) default NULL COMMENT '到点领取的票发送的验证码,每项门票生成的电子票需唯一',
  `verify_time` int(11) NOT NULL COMMENT '验证时间',
  `is_verify_code_invalid` tinyint(1) NOT NULL COMMENT '验证码是否有效',
  `appoint_time` int(11) NOT NULL COMMENT '预约时间',
  `re_appoint_time` int(11) NOT NULL COMMENT '改期申请的日期',
  `re_action_time` int(11) NOT NULL COMMENT '申请退改的时间，只有预约的门票才能申请改签，同步到end_time和begin_time，当天',
  `re_appoint_refuse_reason` text NOT NULL COMMENT '拒绝的理由，由商家或管理员操作',
  `re_appoint_confirm_time` int(11) NOT NULL COMMENT '商家对改的操作时间',
  `re_appoint_status` tinyint(1) NOT NULL COMMENT '0:未申请改期;1:申请改期中;2:确认改期;3:拒改期',
  `supplier_id` int(11) NOT NULL COMMENT '商户ID',
  `is_appoint_time` tinyint(1) NOT NULL COMMENT '时间是否为预约时间',
  `is_divide` tinyint(1) NOT NULL COMMENT '是否为团体票 1:个人票 0团体票',
  `refund_status` tinyint(1) NOT NULL COMMENT '0无退款申请 1退款申请 2.同意 3.拒绝',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `uk` USING BTREE (`verify_code`),
  KEY `ticket_id` USING BTREE (`ticket_id`),
  KEY `order_id` USING BTREE (`order_id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `verify_code` USING BTREE (`verify_code`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  KEY `re_appoint_status` USING BTREE (`re_appoint_status`),
  KEY `is_verify_code_invalid` USING BTREE (`is_verify_code_invalid`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=utf8 COMMENT='电子票验证码表';
