DROP TABLE IF EXISTS `%DB_PREFIX%tour_guide`;
CREATE TABLE `%DB_PREFIX%tour_guide` (
  `id` int(11) NOT NULL auto_increment,
  `user_id` int(11) NOT NULL COMMENT '会员ID',
  `nickname` varchar(100) NOT NULL COMMENT '昵称,冗余字段',
  `title` varchar(255) NOT NULL COMMENT '游记标题',
  `create_time` int(11) NOT NULL COMMENT '发表的时间',
  `image_list` text NOT NULL COMMENT '游记中上传的图片的序列化值, 冗余字段',
  `image_count` int(11) NOT NULL COMMENT '图片数量, 冗余字段',
  `comment_count` int(11) NOT NULL COMMENT '评论的计数(冗余字段)',
  `area_match` text NOT NULL COMMENT '涉及的所有景点小区域标签,拼音',
  `area_match_row` text NOT NULL COMMENT '全文索引原文',
  `is_recommend` tinyint(1) NOT NULL COMMENT '是否被推荐(用于首页等页面的调用)',
  `is_public` tinyint(1) NOT NULL COMMENT '是否已发布（0表示为草稿，即正在创建中 1表示已发布待审核 2已发布已审核）',
  `tour_days` int(11) NOT NULL COMMENT '行程天数',
  `browse_count` int(11) NOT NULL COMMENT '浏览数',
  `update_time` int(11) NOT NULL,
  `recommend_count` int(11) default '0' COMMENT '用户点赞数量',
  `is_hot` tinyint(1) default '0' COMMENT '是/否最热',
  `is_index` tinyint(1) default '0' COMMENT '是否首页显示',
  `tour_range` tinyint(1) default '1' COMMENT '线路区域1.国内2.出境 3.周边游,',
  `tour_type` tinyint(1) default '1' COMMENT '线路类型1.跟团 2.自助 3.自驾',
  PRIMARY KEY  (`id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `is_recommend` USING BTREE (`is_recommend`),
  KEY `is_public` USING BTREE (`is_public`),
  KEY `tour_range` USING BTREE (`tour_range`),
  KEY `tour_type` USING BTREE (`tour_type`),
  FULLTEXT KEY `area_match` (`area_match`)
