DROP TABLE IF EXISTS `%DB_PREFIX%tourline`;
CREATE TABLE `%DB_PREFIX%tourline` (
  `id` int(11) NOT NULL auto_increment,
  `name` varchar(255) NOT NULL COMMENT '线路名称',
  `short_name` varchar(255) NOT NULL COMMENT '缩略名',
  `city_id` int(11) NOT NULL COMMENT '发团城市',
  `city_match` text NOT NULL COMMENT '城市 拼音全文索引 用于城市定位时可在哪些城市检索到',
  `city_match_row` text NOT NULL COMMENT '原文',
  `around_city_match` text NOT NULL COMMENT '周边城市的unicode全文索引',
  `around_city_match_row` text NOT NULL COMMENT '周边城市的原文',
  `province_match` text NOT NULL COMMENT '省份的unicode全文索引',
  `province_match_row` text NOT NULL COMMENT '省份的原文',
  `area_match` text NOT NULL COMMENT '线路包含的大区域name的unicode全文索引',
  `area_match_row` text NOT NULL COMMENT '原文',
  `place_match` text NOT NULL COMMENT '线路所包含小区域name的unicode全文索引',
  `place_match_row` text NOT NULL COMMENT '原文',
  `tag_match` text NOT NULL COMMENT '标签的unicode全文索引',
  `tag_match_row` text NOT NULL COMMENT '标签的原文',
  `origin_price` int(11) NOT NULL COMMENT '显示原价',
  `price` int(11) NOT NULL COMMENT '收费最低起步价',
  `price_explain` text NOT NULL COMMENT '起价说明',
  `sale_total` int(11) NOT NULL COMMENT '销量(实际销量，与线路每日团的销量相加，不包含退款的)',
  `sale_virtual_total` int(11) NOT NULL COMMENT '虚拟销量',
  `satify` int(11) NOT NULL COMMENT '满意度百分比 冗余（所有点评总得分/点评人数*5*10000）保存小数后两位',
  `review_total` int(11) NOT NULL COMMENT '点评总数 冗余',
  `travel_number` int(11) NOT NULL COMMENT '出游人总数',
  `supplier_id` int(11) NOT NULL COMMENT '所属商家（旅行社）ID',
  `image` varchar(255) NOT NULL COMMENT '图片地址',
  `brief` varchar(255) NOT NULL COMMENT '简介',
  `tour_desc` text NOT NULL COMMENT '线路详情',
  `tour_desc_1` text NOT NULL,
  `tour_desc_1_name` varchar(50) NOT NULL,
  `tour_desc_2` text NOT NULL,
  `tour_desc_2_name` varchar(50) NOT NULL,
  `tour_desc_3` text NOT NULL,
  `tour_desc_3_name` varchar(50) NOT NULL,
  `tour_desc_4` text NOT NULL,
  `tour_desc_4_name` varchar(50) NOT NULL,
  `appoint_desc` text NOT NULL COMMENT '预定提示',
  `show_sale_list` tinyint(1) NOT NULL COMMENT '是否显示销售记录',
  `is_effect` tinyint(1) NOT NULL COMMENT '是否下架，即不再显示在前台',
  `is_history` tinyint(1) NOT NULL COMMENT '往期产品,1:可显示不再销售',
  `is_hot` tinyint(1) NOT NULL COMMENT '标识为热卖',
  `is_recommend` tinyint(1) NOT NULL COMMENT '标注为推荐',
  `is_tuan` tinyint(1) NOT NULL COMMENT '是否为团购产品',
  `tuan_cate` int(11) NOT NULL COMMENT '团购分类',
  `tuan_begin_time` int(11) NOT NULL COMMENT '团购开始时间',
  `tuan_end_time` int(11) NOT NULL COMMENT '团购结束时间',
  `tuan_success_count` int(11) NOT NULL COMMENT '成团分数',
  `tuan_is_pre` tinyint(1) NOT NULL COMMENT '是否预告团购',
  `return_money` int(11) NOT NULL COMMENT '旅游完成后返现(订单完成)',
  `return_score` int(11) NOT NULL COMMENT '返还积分',
  `return_exp` int(11) NOT NULL COMMENT '返还经验',
  `sort` int(11) NOT NULL COMMENT '排序',
  `is_review_return` tinyint(1) NOT NULL COMMENT '是否允许点评返还',
  `review_return_money` int(11) NOT NULL COMMENT '点评返现金',
  `review_return_score` int(11) NOT NULL COMMENT '点评返积分',
  `review_return_exp` int(11) NOT NULL COMMENT '点评返经验',
  `is_refund` tinyint(1) NOT NULL COMMENT '确认后是否支持退改',
  `refund_desc` text NOT NULL COMMENT '退改说明',
  `is_expire_refund` tinyint(1) NOT NULL COMMENT '是否允许过期退款',
  `is_visa` tinyint(1) NOT NULL COMMENT '是否提供签证服务',
  `visa_name` varchar(255) NOT NULL COMMENT '签证名称',
  `visa_price` int(11) NOT NULL COMMENT '签证单价',
  `visa_brief` text NOT NULL COMMENT '签证说明',
  `tour_range` tinyint(1) NOT NULL default '1' COMMENT '线路区域1.国内2.出境 3.周边游',
  `tour_type` tinyint(1) NOT NULL default '1' COMMENT '线路类型1.跟团 2.自助 3.自驾',
  `is_rebate` tinyint(1) NOT NULL COMMENT '允许邀请返利',
  `is_buy_return` tinyint(1) NOT NULL COMMENT '允许购物返还',
  `is_namelist` tinyint(1) NOT NULL default '1' COMMENT '出游人员资料是否必填, 0：可不填，1:必填',
  `order_confirm_type` tinyint(1) NOT NULL default '1' COMMENT '订单确认方式 1.付款后手动确认 2.手动确认后付款3.自动确认',
  `advance_day` int(3) NOT NULL COMMENT '提前报名天数',
  `child_norm` text NOT NULL COMMENT '儿童标准',
  `tour_total_day` int(11) NOT NULL COMMENT '行程天数',
  `star_1_count` int(11) NOT NULL COMMENT '点评一星人数',
  `star_2_count` int(11) NOT NULL COMMENT '点评二星人数',
  `star_3_count` int(11) NOT NULL COMMENT '点评三星人数',
  `star_4_count` int(11) NOT NULL COMMENT '点评四星人数',
  `star_5_count` int(11) NOT NULL COMMENT '点评五星人数',
  `seo_title` varchar(255) NOT NULL,
  `seo_keywords` varchar(255) NOT NULL,
  `adv1_name` varchar(255) NOT NULL COMMENT '广告位1title',
  `adv1_image` varchar(255) NOT NULL COMMENT '广告位1图片',
  `adv1_url` varchar(255) NOT NULL COMMENT '广告位1地址',
  `adv2_name` varchar(255) NOT NULL COMMENT '广告位2title',
  `adv2_image` varchar(255) NOT NULL,
  `adv2_url` varchar(255) NOT NULL COMMENT '广告位2地址',
  `seo_description` varchar(255) NOT NULL,
  `show_in_api` tinyint(1) NOT NULL COMMENT '是否显示在api中',
  `tour_guide_key` text NOT NULL COMMENT '用于游记匹配的关键词列表',
  PRIMARY KEY  (`id`),
  KEY `satify` USING BTREE (`satify`),
  KEY `sale_total` USING BTREE (`sale_total`),
  KEY `sale_virtual_total` USING BTREE (`sale_virtual_total`),
  KEY `review_total` USING BTREE (`review_total`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  KEY `show_sale_list` USING BTREE (`show_sale_list`),
  KEY `is_effect` USING BTREE (`is_effect`),
  KEY `is_history` USING BTREE (`is_history`),
  KEY `is_hot` USING BTREE (`is_hot`),
  KEY `is_recommend` USING BTREE (`is_recommend`),
  KEY `is_tuan` USING BTREE (`is_tuan`),
  KEY `tuan_begin_time` USING BTREE (`tuan_begin_time`),
  KEY `tuan_end_time` USING BTREE (`tuan_end_time`),
  KEY `tuan_is_pre` USING BTREE (`tuan_is_pre`),
  KEY `sort` USING BTREE (`sort`),
  KEY `price` USING BTREE (`price`),
  KEY `tour_type` USING BTREE (`tour_type`),
  KEY `tour_range` USING BTREE (`tour_range`),
  KEY `show_in_api` USING BTREE (`show_in_api`),
  FULLTEXT KEY `city_match` (`city_match`),
  FULLTEXT KEY `area_match` (`area_match`),
  FULLTEXT KEY `place_match` (`place_match`),
  FULLTEXT KEY `around_city_match` (`around_city_match`),
  FULLTEXT KEY `province_match` (`province_match`),
