DROP TABLE IF EXISTS `%DB_PREFIX%tourline_order`;
CREATE TABLE `%DB_PREFIX%tourline_order` (
  `id` int(11) NOT NULL auto_increment,
  `tourline_name` varchar(255) NOT NULL COMMENT '线路名称',
  `short_name` varchar(255) NOT NULL COMMENT '商品短名称',
  `tourline_id` int(11) NOT NULL,
  `sn` varchar(255) NOT NULL COMMENT '线路订单号(L_订单号)',
  `adult_price` int(11) NOT NULL COMMENT '成人票单价',
  `adult_sale_price` int(11) NOT NULL COMMENT '在线支付的成人单价',
  `adult_count` int(11) NOT NULL COMMENT '成人预订数',
  `child_price` int(11) NOT NULL COMMENT '儿童票单价',
  `child_sale_price` int(11) NOT NULL COMMENT '在线支付的儿童单价',
  `child_count` int(11) NOT NULL COMMENT '儿童票预定总量',
  `total_price` int(11) NOT NULL COMMENT '如果预付\r\nadult_sale_price*adult_count+child_sale_price*child_count\r\n应付总额\r\nadult_sale_price*adult_count+child_sale_price*child_count\r\n+(adult_count+child_count)*insurance_price+visae_price+visa_count*visa_priceP',
  `tourline_total_price` int(11) NOT NULL COMMENT '线路总价：adult_price*adult_count+child_price*child_count+(adult_count+child_count)*insurance_price+visa',
  `pay_amount` int(11) NOT NULL COMMENT '已付金额(online_pay+voucher_pay+account_pay)',
  `user_id` int(11) NOT NULL COMMENT '会员ID',
  `online_pay` int(11) NOT NULL COMMENT '在线支付金额',
  `voucher_pay` int(11) NOT NULL COMMENT '代金券支付(超出部份不显示，如代金券100，应付50，此处为50，但代金券直接失效，超出不退)',
  `account_pay` int(11) NOT NULL COMMENT '余额支付部份',
  `refund_child_count` int(11) NOT NULL COMMENT '退单儿童人数',
  `refund_adult_count` int(11) NOT NULL COMMENT '退单成人人数',
  `refund_visa_count` int(11) NOT NULL COMMENT '退签服务人数',
  `re_action_time` int(11) NOT NULL COMMENT '用户申请退单退票的时间，成功后清零',
  `refund_amount` int(11) NOT NULL COMMENT '已退金额(退款包含退人数x单价+人数x保险单价)',
  `order_status` tinyint(1) NOT NULL default '1' COMMENT '订单状态(流程)1.新订单 2.已确认 3.已完成 4.作废\r\n新订单：未确认（包含已付款）的都表示为新订单\r\n已确认：表示为商家或管理员查看，确认手动修改\r\n新订单、已确认均可申请退款，否则不可''',
  `pay_status` tinyint(1) NOT NULL COMMENT '支付状态 \r\n0.未支付(代金券或余额支付仍算未支付) \r\n1.已支付(pay_amount>=total_price)',
  `create_time` int(11) NOT NULL COMMENT '下单时间',
  `pay_time` int(11) NOT NULL COMMENT '支付时间',
  `confirm_time` int(11) NOT NULL COMMENT '定单确认时间',
  `over_time` int(11) NOT NULL COMMENT '全部完成时间',
  `appoint_name` varchar(50) NOT NULL COMMENT '预定人姓名',
  `appoint_mobile` varchar(50) NOT NULL COMMENT '预定人手机',
  `appoint_email` varchar(100) NOT NULL COMMENT '预约人邮箱',
  `supplier_id` int(11) NOT NULL COMMENT '商户ID',
  `return_money` int(11) NOT NULL COMMENT '购买订单完成后返现金',
  `return_money_total` int(11) NOT NULL COMMENT '购买订单完成后返现金总数',
  `return_score` int(11) NOT NULL COMMENT '返积分',
  `return_score_total` int(11) NOT NULL,
  `return_exp` int(11) NOT NULL,
  `return_exp_total` int(11) NOT NULL,
  `return_voucher_type_id` int(11) NOT NULL COMMENT '购买后返还的代金券',
  `review_return_money` int(11) NOT NULL COMMENT '点评审核后返现',
  `review_return_score` int(11) NOT NULL COMMENT '点评后返积分',
  `review_return_exp` int(11) NOT NULL COMMENT '点评后增加的经验',
  `review_return_voucher_type_id` int(11) NOT NULL COMMENT '点评后返还的代金券ID',
  `supplier_confirm` tinyint(1) NOT NULL COMMENT '商家是否通过退款审核；0：未确认；1：确认退票；2：拒绝退票；',
  `supplier_confirm_time` int(11) NOT NULL COMMENT '商家审核时间',
  `refuse_reason` varchar(255) NOT NULL COMMENT '拒退的原因',
  `is_refund` tinyint(1) NOT NULL COMMENT '是否支持退单',
  `is_expire_refund` tinyint(1) NOT NULL COMMENT '允许过期未验证退款申请',
  `order_done_day` int(11) NOT NULL COMMENT '订单被确认后XX天未验证自动完成订单',
  `end_time` date NOT NULL COMMENT '验证过期时间，与下单的发团时间同步, 以X日为单位',
  `visa_count` int(11) NOT NULL COMMENT '需签证的人数',
  `visa_price` int(11) NOT NULL COMMENT '签证单价',
  `verify_code` varchar(200) default NULL COMMENT '验证码',
  `verify_time` int(11) NOT NULL COMMENT '用户验证时间',
  `is_verify_code_invalid` tinyint(1) NOT NULL COMMENT '验证码是否为无效',
  `allow_review` tinyint(1) NOT NULL COMMENT '是否允许点评',
  `order_confirm_type` tinyint(1) NOT NULL COMMENT '订单确认方式 1.付款后手动确认 2.手动确认后付款3.自动确认',
  `refund_status` tinyint(1) default '0' COMMENT '0:未申请退款;1:申请退款中;2:确认退款;3:拒退',
  `refund_txt` varchar(255) default NULL COMMENT '用户提交的，退款原因',
  `order_memo` varchar(255) NOT NULL COMMENT '下单时的订单备注，下单时提交，后续不再修改',
  PRIMARY KEY  (`id`),
  UNIQUE KEY `uk_sn` USING BTREE (`sn`),
  UNIQUE KEY `uk_code` USING BTREE (`verify_code`),
  KEY `sn` USING BTREE (`sn`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `order_status` USING BTREE (`order_status`),
  KEY `pay_status` USING BTREE (`pay_status`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `pay_time` USING BTREE (`pay_time`),
  KEY `confirm_time` USING BTREE (`confirm_time`),
  KEY `over_time` USING BTREE (`over_time`),
  KEY `supplier_id` USING BTREE (`supplier_id`),
  KEY `tourline_id` USING BTREE (`tourline_id`),
  KEY `end_time` USING BTREE (`end_time`),
  KEY `allow_review` USING BTREE (`allow_review`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='线路订单表';
