DROP TABLE IF EXISTS `%DB_PREFIX%tuan`;
CREATE TABLE `%DB_PREFIX%tuan` (
  `id` int(11) NOT NULL auto_increment,
  `type` tinyint(1) NOT NULL default '1' COMMENT '团购产品类型1.线路2.门票3.酒店',
