  `origin_price` int(11) NOT NULL COMMENT '同步过来的原价',
  `current_price` int(11) NOT NULL COMMENT '同步过来的现价',
  `discount` int(5) NOT NULL COMMENT '按现价与原价计算所得的折扣(7.5*100)',
  `sale_price` int(11) NOT NULL COMMENT '销售价',
  `image` varchar(255) NOT NULL COMMENT '图片',
  `brief` varchar(255) NOT NULL COMMENT '简介',
  `sale_total` int(11) NOT NULL COMMENT '同步过来的虚拟销量',
  `create_time` int(11) NOT NULL COMMENT '发布时间',
  `begin_time` int(11) NOT NULL COMMENT '团购开始时间',
  `end_time` int(11) NOT NULL COMMENT '团购结束时间',
  `is_pre` tinyint(1) NOT NULL COMMENT '是否预告团购',
  `is_effect` tinyint(1) NOT NULL,
  `success_count` int(11) NOT NULL COMMENT '团购所需人数,同步',
  `cate_id` int(11) NOT NULL COMMENT '团购分类',
  `area_match` text NOT NULL COMMENT '同步的大区索引',
  `place_match` text NOT NULL COMMENT '同步的小区索引',
  `city_match` text NOT NULL COMMENT '同步的所在城市索引',
  `is_history` tinyint(1) NOT NULL COMMENT '是否禁止销售',
  PRIMARY KEY  (`id`),
  KEY `type` USING BTREE (`type`),
  KEY `current_price` USING BTREE (`current_price`),
  KEY `discount` USING BTREE (`discount`),
  KEY `sale_price` USING BTREE (`sale_price`),
  KEY `sale_total` USING BTREE (`sale_total`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `begin_time` USING BTREE (`begin_time`),
  KEY `end_time` USING BTREE (`end_time`),
  KEY `is_pre` USING BTREE (`is_pre`),
  KEY `cate_id` USING BTREE (`cate_id`),
  KEY `rel_id` USING BTREE (`rel_id`),
  FULLTEXT KEY `area_match` (`area_match`),
  FULLTEXT KEY `place_match` (`place_match`),
  FULLTEXT KEY `city_match` (`city_match`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COMMENT='从不同模块同步的团购产品';
