  KEY `money` USING BTREE (`money`),
  KEY `score` USING BTREE (`score`),
  KEY `exp` USING BTREE (`exp`),
  KEY `group_id` USING BTREE (`group_id`),
  KEY `level_id` USING BTREE (`level_id`),
  KEY `province_id` USING BTREE (`province_id`),
  KEY `city_id` USING BTREE (`city_id`),
  KEY `cookie_key` USING BTREE (`cookie_key`),
  KEY `follow_count` USING BTREE (`follow_count`),
  KEY `fans_count` USING BTREE (`fans_count`),
  KEY `tour_guide_count` USING BTREE (`tour_guide_count`),
  KEY `review_count` USING BTREE (`review_count`),
  KEY `tourline_order_count` USING BTREE (`tourline_order_count`),
  KEY `ticket_order_count` USING BTREE (`ticket_order_count`),
  KEY `msg_count` USING BTREE (`msg_count`),
  KEY `new_msg_count` USING BTREE (`new_msg_count`),
