DROP TABLE IF EXISTS `%DB_PREFIX%user_msg`;
CREATE TABLE `%DB_PREFIX%user_msg` (
  `id` int(11) NOT NULL auto_increment,
  `user_id` int(11) NOT NULL COMMENT '会员ID',
  `msg_title` varchar(100) NOT NULL COMMENT '消息标题',
  `msg_content` text NOT NULL COMMENT '消息内容',
  `msg_time` int(11) NOT NULL COMMENT '消息发生时间',
  `is_read` tinyint(1) NOT NULL COMMENT '是否已读(0:未读 1:已读)',
  `system_msg_id` int(11) default NULL COMMENT '系统消息ID',
  `is_delete` tinyint(1) NOT NULL,
  PRIMARY KEY  (`id`),
  UNIQUE KEY `unk` USING BTREE (`user_id`,`system_msg_id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `msg_time` USING BTREE (`msg_time`),
  KEY `is_read` USING BTREE (`is_read`),
  KEY `system_msg_id` USING BTREE (`system_msg_id`),
  KEY `is_delete` USING BTREE (`is_delete`)
) ENGINE=MyISAM AUTO_INCREMENT=30 DEFAULT CHARSET=utf8 COMMENT='会员消息表(站内通知)';
DROP TABLE IF EXISTS `%DB_PREFIX%user_namelist`;
CREATE TABLE `%DB_PREFIX%user_namelist` (
