DROP TABLE IF EXISTS `%DB_PREFIX%user_rebate`;
CREATE TABLE `%DB_PREFIX%user_rebate` (
  `id` int(11) NOT NULL auto_increment,
  `user_id` int(11) NOT NULL COMMENT '会员ID',
  `from_uid` int(11) NOT NULL COMMENT '返利来源的会员ID',
  `nickname` varchar(255) NOT NULL COMMENT '返利来源用户的昵称',
  `from_oid` int(11) NOT NULL COMMENT '返利来源的订单ID',
  `from_otype` tinyint(1) NOT NULL default '1' COMMENT '返利来源的订单类型(1:旅游产品 2.景点门票 ...后续可扩展)',
  `money` int(11) NOT NULL COMMENT '返利的钱',
  `create_time` int(11) NOT NULL COMMENT '返利产生的时间',
  `pay_time` int(11) NOT NULL COMMENT '返利发放的时间（一般实时发放，后续需求有作人工审核或延时发放再作利用）',
  PRIMARY KEY  (`id`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `from_uid` USING BTREE (`from_uid`),
  KEY `from_oid` USING BTREE (`from_oid`),
  KEY `from_otype` USING BTREE (`from_otype`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `pay_time` USING BTREE (`pay_time`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='会员邀请并产生购物的返利表';
DROP TABLE IF EXISTS `%DB_PREFIX%voucher`;
CREATE TABLE `%DB_PREFIX%voucher` (
  `id` int(11) NOT NULL auto_increment,
  `voucher_name` varchar(100) NOT NULL COMMENT '代金券名称',
  `create_time` int(11) NOT NULL COMMENT '领取（发放）时间',
  `end_time` int(11) NOT NULL COMMENT '过期时间',
  `user_id` int(11) NOT NULL COMMENT '所属的会员',
  `is_used` tinyint(1) NOT NULL COMMENT '是否已使用',
  `use_otype` tinyint(1) NOT NULL COMMENT '使用的订单类型(1:旅游产品订单 2.门票产品 3:商城商品订单...后续可扩展)',
  `use_oid` int(11) NOT NULL COMMENT '使用于的定单ID',
  `use_time` int(11) NOT NULL COMMENT '使用时间',
  `money` int(11) NOT NULL COMMENT '面额',
  `voucher_type_id` int(11) NOT NULL COMMENT '代金券类型ID',
  `is_effect` tinyint(1) NOT NULL COMMENT '是否有效',
  PRIMARY KEY  (`id`),
  KEY `create_time` USING BTREE (`create_time`),
  KEY `end_time` USING BTREE (`end_time`),
  KEY `user_id` USING BTREE (`user_id`),
  KEY `is_used` USING BTREE (`is_used`),
  KEY `voucher_type_id` USING BTREE (`voucher_type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='代金券列表';
