DROP TABLE IF EXISTS `%DB_PREFIX%voucher_promote`;
CREATE TABLE `%DB_PREFIX%voucher_promote` (
  `voucher_type_id` int(11) NOT NULL COMMENT '代金券类型ID',
  `voucher_promote` tinyint(1) NOT NULL default '1' COMMENT '1.门票 2.酒店 3.线路 可扩展',
  `voucher_rel_id` int(11) NOT NULL COMMENT '相应的模块ID,如门票ID，线路ID',
  `voucher_promote_type` tinyint(1) NOT NULL default '1' COMMENT '1.订单完成后返还 2.点评审核后返还',
  KEY `voucher_type_id` USING BTREE (`voucher_type_id`),
  KEY `voucher_promote` USING BTREE (`voucher_promote`),
  KEY `voucher_rel_id` USING BTREE (`voucher_rel_id`),
  KEY `voucher_promote_type` USING BTREE (`voucher_promote_type`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='与产品相关联，用于营销性质的代金券发放，如购物并成功后返还100元代金券的功能';
DROP TABLE IF EXISTS `%DB_PREFIX%voucher_type`;
CREATE TABLE `%DB_PREFIX%voucher_type` (
  `id` int(11) NOT NULL auto_increment,
  `voucher_name` varchar(100) NOT NULL COMMENT '代金券名称',
  `money` int(11) NOT NULL COMMENT '代金券面额',
  `deliver_count` int(11) NOT NULL COMMENT '已发放数量',
  `deliver_limit` int(11) NOT NULL COMMENT '发放限量(如为0表示不限量发放, deliver_type为3的自定义发放不限量)',
  `is_promote` tinyint(1) NOT NULL COMMENT '营销代金券，用于旅游产品，门票，酒店等购买时作为返现的凭证,为1是不限量发放',
  `deliver_type` tinyint(1) NOT NULL default '1' COMMENT '发放类型（1:按会员等级发放 2:按会员组发放 3.自定义发放，即用于提供给产品购物完的自动发放)',
  `deliver_rel_id` int(11) NOT NULL COMMENT '按会员组或等级发放时相关的等级或组的ID',
  `deliver_end_time` int(11) NOT NULL COMMENT '领取或发放的截止日期(0表示无限期 deliver_type为3的自定义发放不限期)',
  `is_effect` tinyint(1) NOT NULL COMMENT '是否可领取（无效则不可领取，购物后不再返还，也不再前台显示，但不影响已领取的代金券）',
  `voucher_end_time` int(11) NOT NULL COMMENT '领取的代金券的使用有效期(0表示无限期)',
  `sort` int(11) NOT NULL COMMENT '排序',
  PRIMARY KEY  (`id`),
  KEY `deliver_type` USING BTREE (`deliver_type`),
  KEY `deliver_rel_id` USING BTREE (`deliver_rel_id`),
  KEY `deliver_end_time` USING BTREE (`deliver_end_time`),
  KEY `sort` USING BTREE (`sort`),
  KEY `is_effect` USING BTREE (`is_effect`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='代金券分组表，用于手动发放会员领取或者自定义发放自动领取，见deliver_type';
